mapper.update:
   	UPDATE lc_board 
	SET b_group	= #{bId}
  	WHERE b_id = #{bId}

UPDATE lc_board 
SET b_title		=	#{bTitle},
	b_content	=	#{bContent}
WHERE b_id = #{bId}

mapper.insert:
INSERT INTO lc_comment (
	b_id,
	c_id,
	c_comment,
	c_writer
)VALUES(
	#{bId},
	#{cId},
	#{cComment},
	#{cWriter}
)
ON DUPLICATE KEY UPDATE
	c_comment = #{cComment}

mapper.update:
UPDATE 	lc_comment
SET 	c_comment = #{cComment}
WHERE 	c_id = #{cId}

		UPDATE 	lc_board
